import Bowser from 'bowser';

declare enum BrowserName {
    CHROME = "Chrome",
    FIREFOX = "Firefox",
    EDGE = "Microsoft Edge",
    SAFARI = "Safari"
}
declare enum OSName {
    WINDOWS = "Windows",
    MAC = "macOS",
    LINUX = "Linux"
}
declare class BrowserInfo {
    private static browser;
    static getBrowserDetails(): Bowser.Parser.BrowserDetails;
    static getOSDetails(): Bowser.Parser.OSDetails;
    static getPlatformDetails(): Bowser.Parser.PlatformDetails;
    static getEngineDetails(): Bowser.Parser.EngineDetails;
    static isChrome(): boolean;
    static isFirefox(): boolean;
    static isEdge(): boolean;
    static isSafari(): boolean;
    static isWindows(): boolean;
    static isMac(): boolean;
    static isLinux(): boolean;
    static isVersionGreaterThan(version: string): boolean;
    static isVersionGreaterThanOrEqualTo(version: string): boolean;
    static isVersionLessThan(version: string): boolean;
    static isVersionLessThanOrEqualTo(version: string): boolean;
    static isSubVersionOf(version: string): boolean;
}

declare enum CapabilityState {
    NOT_CAPABLE = "not capable",
    CAPABLE = "capable",
    UNKNOWN = "unknown"
}
declare class WebCapabilities {
    static isCapableOfBackgroundNoiseRemoval(): CapabilityState;
    static isCapableOfVirtualBackground(): CapabilityState;
    static isCapableOfReceiving1080pVideo(): CapabilityState;
    static isCapableOfSending1080pVideo(): CapabilityState;
}

declare class CpuInfo {
    static getNumLogicalCores(): number | undefined;
}

export { BrowserInfo, BrowserName, CapabilityState, CpuInfo, OSName, WebCapabilities };
